$(function() {
	form1 = $('form[name=form1]'),
		prev = $('input[name=cho_Province]', form1),
		city = $('input[name=cho_City]', form1),
		area = $('input[name=cho_Area]', form1),
		vale = ['请选择企业总部所在省或直辖市', '请选择企业总部所在城市', '请选择地区'];
	form1.submit(function() {
		if(prev.val() == vale[0]) {
			alert(vale[0]);
			return false;
		};
		if(city.val() == vale[1]) {
			alert(vale[1]);
			return false;
		};
		if(area.val() == vale[2]) {
			alert(vale[2]);
			return false;
		}
	});
});

